parameters.jsCode:
// 【数据预处理】
// 将 FastGPT 传来的历史记录数组，格式化为字符串

const body = $input.item.json.body || {};
const historyArray = body.history || [];
const currentMsg = body.message || "";

// 只取最近 6 条记录，避免 Token 爆炸
const recentHistory = historyArray.slice(-6);

// 格式化为对话脚本
let historyText = recentHistory.map(h => `${h.role}: ${h.content}`).join("\n");

return {
  current_msg: currentMsg,
  history_context: historyText || "(无历史记录，这是第一句)"
};

parameters.body:
={{ JSON.stringify({
  model: "deepseek-chat",
  messages: [
    {
      role: "system",
      content: "你是一个顶级的【提示词优化专家】(Meta-Prompt Engineer)。\n你的任务是根据用户的【聊天历史】和【当前问题】，为另一个AI生成最佳的配置。\n\n请分析用户的意图、情绪和隐含需求，然后输出一个 JSON：\n1. generated_system_prompt: 为执行者AI编写的人设和指令。例如：如果用户在抱怨，就生成一个安抚型人设；如果用户在写代码，就生成一个资深工程师人设。\n2. suggested_temperature: 建议的温度值 (0.0 - 1.5)。严谨任务用低温，创意任务用高温。\n\n【必须输出纯 JSON，不要包含 Markdown 标记】"
    },
    {
      role: "user",
      content: "### 聊天历史:\n" + $json.history_context + "\n\n### 当前问题:\n" + $json.current_msg
    }
  ],
  temperature: 0.7,
  response_format: { type: "json_object" }
}) }}

parameters.jsCode:
// 【解析配置】
// 提取军师AI生成的 Prompt 和 Temperature
try {
  const aiContent = $input.item.json.choices[0].message.content;
  const parsed = JSON.parse(aiContent);

  return {
    // 这里的 Prompt 是 AI 刚刚为你生成的！
    final_prompt: parsed.generated_system_prompt,
    // 这里的 Temperature 也是 AI 决定的！
    final_temp: parsed.suggested_temperature || 0.7,
    user_msg: $('2. 格式化上下文').item.json.current_msg
  };
} catch (e) {
  // 兜底策略
  return {
    final_prompt: "你是一个智能助手。",
    final_temp: 0.5,
    user_msg: $('2. 格式化上下文').item.json.current_msg
  };
}

parameters.body:
={{ JSON.stringify({
  model: "deepseek-chat",
  messages: [
    {
      role: "system",
      content: $json.final_prompt
    },
    {
      role: "user",
      content: $json.user_msg
    }
  ],
  // 这里的温度是动态的！
  temperature: $json.final_temp
}) }}

parameters.responseBody:
={{ 
// 【终极修复】使用 JSON.stringify 自动处理转义
JSON.stringify({
  answer: $json.choices[0].message.content,
  meta_info: {
     optimized_prompt: $('4. 提取优化配置').item.json.final_prompt,
     optimized_temp: $('4. 提取优化配置').item.json.final_temp
  }
})
}}
